INSERT INTO `table_post` (`id`, `ten`, `tenkhongdau`, `photo`, `mota`, `noidung`, `loai`, `link`, `ngaytao`) VALUES
(1,	'[CÔNG TY TNHH SRITHAI VIETNAM] Ngày hội mua hàng giảm giá Chào Xuân Bính Thân 2016 14 - 17/01/2016',	'cong-ty-tnhh-srithai-vietnam-ngay-hoi-mua-hang-giam-gia-chao-xuan-binh-than-2016-14-17012016',	'/en/upload/images/Melamine/toroi-01.jpg',	'\n	Công ty TNHH Srithai Viet nam xin trân trọng thông báo:\n\n	- Nhân dịp chào mừng năm mới Xuân Bính Thân 2016, Công ty TNHH Srithai Việt Nam tổ chức Ngày hội bán hàng giảm giá, nhằm mục đích quảng bá, giới thiệu hình ảnh, sản phẩm của Công ty đến với người tiêu dùng. Giảm giá từ 20 - 50% các mặt hàng nhựa Thái Lan cao ...',	'\n                <p class=\"news_title\">[CÔNG TY TNHH SRITHAI VIETNAM] Ngày hội mua hàng giảm giá Chào Xuân Bính Thân 2016 14 - 17/01/2016</p>\n                <p class=\"news_date\">( Đăng lúc 2016-01-07 10:47:15 )</p>\n                <p>\n	<strong>Công ty TNHH Srithai Viet nam xin trân trọng thông báo:</strong></p>\n<p>\n	- Nhân dịp chào mừng năm mới Xuân Bính Thân 2016, Công ty TNHH Srithai Việt Nam tổ chức<span style=\"color:#ff0000;\"><strong> Ngày hội bán hàng giảm giá</strong></span>, nhằm mục đích quảng bá, giới thiệu hình ảnh, sản phẩm của Công ty đến với người tiêu dùng. Giảm giá từ 20 - 50% các mặt hàng nhựa Thái Lan cao cấp.</p>\n<p>\n	<strong><span style=\"color:#ff0000;\">- Thời gian từ 14 - 17/01/2016.</span></strong></p>\n<p>\n	<strong><span style=\"color:#ff0000;\">- Giờ mở cửa: 9h - 20h</span></strong></p>\n<p>\n	<strong><span style=\"color:#ff0000;\">- Địa điểm: Công ty Srithai Viet Nam - Số 9, Đường số 2, KCN Sóng Thần 1, Dĩ An, Bình Dương.</span></strong></p>\n<p>\n	- Mời quý khách hàng, bà con, cô bác tới tham quan, mua sắm để mang về những phần quà cho người thân và gia đình trong dịp năm mới 2016.</p>\n<p>\n	Xin trân trọng cảm ơn.</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Melamine/toroi-01.jpg\" style=\"width: 596px; height: 842px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Melamine/toroi-02.jpg\" style=\"width: 596px; height: 842px;\"></p>\n                <div class=\"clearfix\"></div>\n            ',	NULL,	'http://www.srithaivietnam.com/vi/cong-ty-tnhh-srithai-vietnam-ngay-hoi-mua-hang-giam-gia-chao-xuan-binh-than-2016-14-17012016/n11',	1553101686),
(2,	'Hội chợ Mua sắm & Ẩm thực Thái lan - Trung tâm triễn lãm Tân Bình',	'hoi-cho-mua-sam-am-thuc-thai-lan-trung-tam-trien-lam-tan-binh',	'/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01648.jpg',	'\n	Hội chợ Mua sắm & Ẩm thực Thái lan \n\n	Cục Xúc tiến Thương mại, Bộ Thương mại Thái Lan - Trung tâm Thương mại Thái Lan tại Thành phố Hồ Chí Minh phối hợp với SQM tổ chức \"Hội chợ Mua sắm & Ẩm thực Thái lan\" từ ngày 10 đến ngày 14 tháng 12 năm 2014 tại Triển lãm Tân Bình và Trung tâm Hội nghị - 446 ...',	'\n                <p class=\"news_title\">Hội chợ Mua sắm &amp; Ẩm thực Thái lan - Trung tâm triễn lãm Tân Bình</p>\n                <p class=\"news_date\">( Đăng lúc 2014-12-12 11:45:10 )</p>\n                <h2 class=\"sapo\" style=\"outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; color: rgb(51, 51, 51); font-family: \'Times New Roman\'; text-align: justify; font-size: 14px !important;\">\n	Hội chợ Mua sắm &amp; Ẩm thực Thái lan </h2>\n<p style=\"text-align: justify;\">\n	Cục Xúc tiến Thương mại, Bộ Thương mại Thái Lan - Trung tâm Thương mại Thái Lan tại Thành phố Hồ Chí Minh phối hợp với SQM tổ chức \"Hội chợ Mua sắm &amp; Ẩm thực Thái lan\" từ ngày 10 đến ngày 14 tháng 12 năm 2014 tại Triển lãm Tân Bình và Trung tâm Hội nghị - 446 Hoàng Văn Thụ, Quận Tân Bình, Thành phố Hồ Chí Minh.</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01648.jpg\" style=\"width: 400px; height: 226px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01660.jpg\" style=\"width: 374px; height: 226px;\"></p>\n<p style=\"text-align: center;\">\n	Hình ảnh khai mạc hội chợ</p>\n<hr>\n<p style=\"text-align: justify;\">\n	Năm nay, gần 250 công ty với 300 gian hàng tham gia Triển lãm Thương mại Thái Lan tại Thành phố Hồ Chí Minh để giới thiệu một loạt các sản phẩm chất lượng cao với ngành nghề kinh doanh chính nổi tiếng của Thái Lan bao gồm: sản phẩm nhựa (Linh kiện, phụ tùng ô tô, xe máy, chất dẻo); Điện gia dụng; Thực phẩm / đồ uống; Sức khỏe và vẻ đẹp; Quà tặng và đồ trang trí, đồ chơi trẻ em, đồ nội thất, đồ trong nhà tắm và nhà bếp, v.v...</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01664.jpg\" style=\"width: 490px; height: 250px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01665.jpg\" style=\"width: 490px; height: 250px;\"></p>\n<p style=\"text-align: center;\">\n	Srithai Vietnam Melamine booth in Exhibition</p>\n<p style=\"text-align: center;\">\n	Số M6-7-8-9</p>\n<hr>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01685.jpg\" style=\"width: 490px; height: 250px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01689.jpg\" style=\"width: 490px; height: 250px;\"></p>\n<p style=\"text-align: center;\">\n	Srithai Vietnam Melamine booth in Exhibition</p>\n<hr>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01691.jpg\" style=\"width: 450px; height: 250px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01704.jpg\" style=\"height: 250px; width: 140px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/DSC01705.jpg\" style=\"height: 250px; width: 140px;\"> <img alt=\"\" src=\"http://www.srithaivietnam.com/en/upload/images/Exhibition/Tan%20Binh%2012-2014/Untitled-1.jpg\" style=\"width: 140px; height: 255px;\"></p>\n<p style=\"text-align: center;\">\n	Giới thiệu sản phẩm</p>\n<p style=\"text-align: center;\">\n	Srithai Vietnam Stainless booth in Exhibition</p>\n<p style=\"t